import React, { useState, useEffect } from "react";
import { fetchDailyData, fetchCharts } from "../../api";

// chart.js
import { Line, Bar, Doughnut } from "react-chartjs-2";

//material
import { Card, CardContent, Typography, Grid } from "@material-ui/core";

// style
import styles from "./Chart.module.css";
import cx from "classnames";

// useState et useEffect sont des hooks (synonymes de fonctions)

// get data charts
// const [charts, setCharts] = useState([]);

// useEffect(() => {
//   const fetchChartsLocal = async () => {
//     setCharts(await fetchCharts());
//   };
//   fetchChartsLocal();
// }, []);

const Chart = () => {
  const [dailyData, setDailyData] = useState([]);

  // On peut pas faire des fonctions asynchrones dans notre hook useEffect donc on va créer une fonction asynchrone à l'intérieur
  // Donc on créer une fonction fetchAPI asynchrone  qui appel notre fonction fetchDailyData qui retourne une promesse
  // car c'est une fonciton asynchrone et le await juste avant permet d'attendre puis on le met dans notre state setDailyData.
  useEffect(() => {
    const fetchAPI = async () => {
      setDailyData(await fetchDailyData());
    };

    fetchAPI(); // on appel la fonction pour l'activer
  }, []);

  // 2 chart : barChart -> graphique pour chaque pays différents et lineChart -> le global quand on arrive sur le site

  const lineChart = dailyData.length ? (
    <Line
      data={{
        labels: dailyData.map(({ date }) => date),
        datasets: [
          {
            data: dailyData.map(({ confirmed }) => confirmed),
            label: "Infected",
            borderCorlor: "#3333ff",
            fill: true,
          },
          {
            data: dailyData.map(({ deaths }) => deaths),
            label: "Deaths",
            borderCorlor: "red",
            backgroundColor: "yellow",
            fill: true,
          },
        ],
      }}
    ></Line>
  ) : null;

  const barChart = dailyData.length ? (
    <Bar
      data={{
        labels: ["Red", "Yellow", "Pink", "Brown"],
        datasets: [
          {
            label: "Notes sur 20",
            data: ["12", "5", "20", "18", "2"],
            backgroundColor: [
              "rgba(255, 99, 132, 0.2)",
              "rgba(54, 162, 235, 0.2)",
              "rgba(255, 206, 86, 0.2)",
              "rgba(75, 192, 192, 0.2)",
            ],
          },
        ],
      }}
    ></Bar>
  ) : null;

  const doughnutChart = dailyData.length ? (
    <Bar
      data={{
        labels: ["Red", "Yellow", "Pink", "Brown"],
        datasets: [
          {
            label: "Notes sur 20",
            data: ["12", "5", "20", "18", "2"],
            backgroundColor: [
              "rgba(255, 99, 132, 0.2)",
              "rgba(54, 162, 235, 0.2)",
              "rgba(255, 206, 86, 0.2)",
              "rgba(75, 192, 192, 0.2)",
            ],
          },
        ],
      }}
    ></Bar>
  ) : null;
  // Doughnut

  return (
    <Grid container justify="space-between">
      <Grid item classNames={cx(styles.card, styles.firstChart)} xs={12} md={4}>
        {lineChart}
      </Grid>
      <Grid
        item
        classNames={cx(styles.card, styles.secondChart)}
        xs={12}
        md={4}
      >
        {barChart}
      </Grid>
      <Grid
        item
        classNames={cx(styles.card, styles.secondChart)}
        xs={12}
        md={4}
      >
        {doughnutChart}
      </Grid>
    </Grid>
  );
};

export default Chart;
