import React from "react";

// Material equivalent de Bootstrap
import { Card, CardContent, Typography, Grid, styled } from "@material-ui/core";

import CountUp from "react-countup";
import cx from "classnames";

//css
import styles from "./Cards.module.css";

// On recupère les données avev PROPS
// Cards = {props} => on le destructure comme ça on peut acceder directement aux valeurs de data (dont à a accés depuis App.js)
const Cards = ({ data: { confirmed, recovered, deaths, lastUpdate } }) => {
  if (!confirmed) {
    // si confirmed n'est pas encore chargé alors on retourne Loading...
    return "Loading...";
  }
  // sinon
  return (
    <div className={styled.container}>
      {/* Sur les petits devices (xs = 12) 12 veut dire que ça prendra toute la place de l'écran */}
      {/* md={3} veut dire que sur les écrans moyen et + grands ça prendra que 3/12eme de la place de l'écran */}
      <Grid container justify="center">
        <Grid
          item
          component={Card}
          classNames={cx(styles.card, styles.infected)}
          xs={12}
          md={3}
          spacing={3}
        >
          <CardContent>
            <Typography color="textSeconday"> Infected</Typography>
            <Typography variant="h5">
              <CountUp
                start={0}
                end={confirmed.value}
                duration={2.5}
                separator={","}
              />
            </Typography>{" "}
            {/* {confirmed.value}  */}
            <Typography color="textSeconday">
              {new Date(lastUpdate).toDateString()}
            </Typography>
            <Typography variant="body2"> Number of covid blabla</Typography>
          </CardContent>
        </Grid>

        {/*  */}

        <Grid
          item
          component={Card}
          classNames={cx(styles.card, styles.recovered)}
          xs={12}
          md={3}
        >
          <CardContent>
            <Typography color="textSeconday"> Recovered</Typography>
            <Typography variant="h5">
              {" "}
              <CountUp
                start={0}
                end={recovered.value}
                duration={2.5}
                separator={","}
              />
            </Typography>
            <Typography color="textSeconday">
              {" "}
              {new Date(lastUpdate).toDateString()}
            </Typography>
            <Typography variant="body2">
              {" "}
              Number of Recovered covid blabla
            </Typography>
          </CardContent>
        </Grid>

        {/*  */}

        <Grid
          item
          component={Card}
          classNames={cx(styles.card, styles.deaths)}
          xs={12}
          md={3}
        >
          <CardContent>
            <Typography color="textSeconday"> Death</Typography>
            <Typography variant="h5">
              {" "}
              <CountUp
                start={0}
                end={deaths.value}
                duration={2.5}
                separator={","}
              />
            </Typography>
            <Typography color="textSeconday">
              {" "}
              {new Date(lastUpdate).toDateString()}
            </Typography>
            <Typography variant="body2">
              {" "}
              Number of Death covid blabla
            </Typography>
          </CardContent>
        </Grid>
      </Grid>
    </div>
  );
};

export default Cards;
