import React, { useState, useEffect } from "react";
import { fetchDailyData } from "../../api";

// useState et useEffect sont des hooks (synonymes de fonctions)

const Chart = () => {
  const [dailyData, setDailyData] = useState({});

  // On peut pas faire des fonctions asynchrones dans notre hook useEffect donc on va créer une fonction asynchrone à l'intérieur
  // Donc on créer une fonction fetchAPI asynchrone avec notre state dailyData qui appel notre fonction fetchDailyData qui retourne une promesse
  // car c'est une fonciton asynchrone et le await juste avant permet d'attendre puis on le met dans notre state
  useEffect(() => {
    const fetchAPI = async () => {
      setDailyData(await fetchDailyData());
    };
  });
  return <h1>Chart</h1>;
};

export default Chart;
